class MenuScene extends Phaser.Scene{constructor(){super("MenuScene")}create(){this.gw=gameWidth;this.gh=gameHeight;this.audio=this.game.audio;this.addBackground();this.addBuble();this.addBackgroundComponents();this.addRiskyJumperText();this.addPlayButton();this.addSettingsButton();this.addRankingButton();this.addAchievementsButton();this.audio.mainMenu.play()}update(){this.bottomBelt1.tilePositionX+=3;this.bottomBelt2.tilePositionX+=1;this.buble.tilePositionX+=2}addBackground(){this.add.image(0,0,"background").setOrigin(0,0).setDisplaySize(this.gw,this.gh)}addTopBelt(){this.add.image(this.gw/15,0,"topBelt").setOrigin(0,0)}addBuble(){this.buble=this.add.tileSprite(this.gw/2,this.gh/2,698,1227,"buble")}addBottomBelt1(){this.bottomBelt1=this.add.tileSprite(0,this.gh,720,208,"bottomBelt1").setOrigin(0,1)}addBottomBelt2(){this.bottomBelt2=this.add.tileSprite(0,this.gh,720,468,"bottomBelt2").setOrigin(0,1)}addRiskyJumperText(){this.add.image(this.gw/2,this.gh/4,"riskyJumperText")}addSettingsButton(){new Button(this,this.gw/2-this.gw/3,this.gh-this.gh/3+250,"settingsButton").onClick(()=>{this.audio.click.play();this.scene.bringToTop("SettingsScene");this.scene.setVisible(true,"SettingsScene");this.scene.resume("SettingsScene");if(!this.scene.isPaused("SettingsScene")){this.scene.launch("SettingsScene")}})}addRankingButton(){new Button(this,this.gw/2,this.gh-this.gh/3+250,"rankingButton").onClick(()=>{this.audio.click.play()})}addAchievementsButton(){new Button(this,this.gw/2+this.gw/3,this.gh-this.gh/3+250,"achivButton").onClick(()=>{this.audio.click.play();this.changeScene("RankScene","MenuScene")})}changeScene(key,key2){const scene=this.scene.get(key);this.scene.pause();this.scene.setVisible(false,key2);this.scene.setVisible(true,key);scene.scene.restart({currentScene:key2});this.scene.bringToTop(key);if(!this.scene.isPaused(key)){this.scene.launch(key,{currentScene:key2})}}addBackgroundComponents(){this.addBottomBelt2();this.addBottomBelt1();this.addTopBelt()}addPlayButton(){const button=new Button(this,this.gw/2,this.gh-this.gh/3,"playButton").onClick(()=>{StatusBar.backgroundColorByHexString("#00000000");this.audio.mainMenu.stop();this.audio.click.play();this.scene.sleep("MenuScene").resume("PlayScene").swapPosition("MenuScene","PlayScene");this.scene.setVisible(false,"MenuScene")});this.tweens.add({targets:button,ease:"Power2",scaleX:1.1,scaleY:1.1,duration:500,yoyo:true,repeat:-1})}}