class RankScene extends Phaser.Scene{constructor(){super("RankScene")}create({currentScene}){this.gw=gameWidth;this.gh=gameHeight;this.bestScore=Number(localStorage.getItem("bestScore"))||0;this.previousScene=currentScene;this.audio=this.game.audio;this.addBackground(0,0);this.addRankText(this.gw/2,this.gh/7);this.addMiddleLayer(this.gw/2,this.gh/2);this.addMedalColor(this.gw/2,this.gh/2);this.addMedal(this.gw/2,this.gh/2);this.addBackButton();this.medalColors=[9127187,7568545,16170496,205];this.updateMedalColor()}addBackground(x,y){this.add.image(x,y,"topBeltHorizontal").setOrigin(0,0).setDisplaySize(this.gw,this.gh)}addRankText(x,y){this.add.image(x,y,"rankScreenRankText")}addMiddleLayer(x,y){this.add.image(x,y,"rankScreenMiddle")}addMedalColor(x,y){this.middle=this.add.image(x,y,"rankScreenMedalColor").setTint(9127187)}addMedal(x,y){this.add.image(x,y,"rankScreenMedal")}updateMedalColor(){this.medalColors.forEach((color,index)=>{if(this.bestScore>=(index+1)*20)this.middle.setTint(color)})}addBackButton(){new Button(this,this.gw/2,this.gh-100,"backButton").onClick(()=>{this.audio.click.play();this.scene.setVisible(false,"RankScene");this.scene.bringToTop(this.previousScene);this.scene.resume(this.previousScene);this.scene.setVisible(true,this.previousScene);this.scene.pause()})}}