class PauseScene extends Phaser.Scene{constructor(){super("PauseScene")}create({currentScene}){this.gw=gameWidth;this.gh=gameHeight;this.previousScene=currentScene;this.audio=this.game.audio;this.addBackground(0,0);this.addPauseText(this.gw/2,this.gh/8);this.addRestartButton(this.gw/2,this.gh/2);this.addSettingsButton(this.gw/2,this.gh/2+150);this.addBackButton(this.gw/2,this.gh-100)}addBackground(x,y){this.add.image(x,y,"topBeltHorizontal").setOrigin(0,0).setDisplaySize(this.gw,this.gh)}addPauseText(x,y){this.add.image(x,y,"pauseText")}addSettingsButton(x,y){new Button(this,x,y,"settingsButton2").onClick(()=>{this.audio.click.play();this.changeScene("SettingsScene")})}addBackButton(x,y){new Button(this,x,y,"backButton").onClick(()=>{this.backToPreviousScene()})}addRestartButton(x,y){new Button(this,x,y,"replayButton").onClick(()=>{let playScene=this.scene.get("PlayScene");playScene.scene.restart();this.backToPreviousScene()})}backToPreviousScene(){this.audio.click.play();this.scene.setVisible(false,"PauseScene");this.scene.bringToTop(this.previousScene);this.scene.resume(this.previousScene);this.scene.setVisible(true,this.previousScene);this.scene.pause()}changeScene(key){this.scene.setVisible(true,key);this.scene.bringToTop(key);this.scene.resume(key);if(!this.scene.isPaused(key)){this.scene.launch(key)}}}